import './App.css';
import React, { Fragment, useState } from 'react';

import {
  BrowserRouter as Router,
  Route,
  Routes,
  Navigate,
} from 'react-router-dom';

import Topbar from './components/topbar/Topbar';
import Sidebar from './components/sidebar/Sidebar';
import Dashboard from './components/dashboard/Dashboard';
import Home from './components/dashboard/pages/home/Home';
import Login from './components/auths/Login';
import Landing from './components/landing/Landing';
import Borrowers from './components/dashboard/pages/home/borrowers/Borrowers';
import Register from './components/auths/Register';
import Borrower from './components/dashboard/pages/home/borrowers/Borrower';
import EditBorrower from './components/dashboard/pages/home/borrowers/EditBorrower';
import GetAllLoans from './components/dashboard/pages/loans/Loans';
import AddLoan from './components/dashboard/pages/loans/AddLoan';
import AddBorrower from './components/dashboard/pages/borrowers/AddBurrower';

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  const setAuth = (boolean) => {
    setIsAuthenticated(boolean);
  };
  return (
    <Router>
      <div className='App'>
        <Topbar />
        <div>
          <Fragment>
            <Routes>
              {/* LANDING */}
              <Route
                exact
                path='/'
                element={
                  !isAuthenticated ? <Landing /> : <Navigate to='/login' />
                }
              ></Route>

              {/* REGISTER */}
              <Route
                exact
                path='/register'
                element={
                  !isAuthenticated ? (
                    <Register setAuth={setAuth} />
                  ) : (
                    <Navigate to='/home' />
                  )
                }
              ></Route>

              {/* LOGIN */}
              <Route
                exact
                path='/login'
                element={
                  !isAuthenticated ? (
                    <Login setAuth={setAuth} />
                  ) : (
                    <Navigate to='/dashboard' />
                  )
                }
              ></Route>

              {/* HOME */}
              <Route exact path='/home'>
                <Route exact path='/home' element={<Home />}></Route>
              </Route>

              {/* DASHBOARD */}
              <Route
                exact
                path='/dashboard'
                element={
                  isAuthenticated ? (
                    <Dashboard setAuth={setAuth} />
                  ) : (
                    <Navigate to='/login' />
                  )
                }
              ></Route>

              {/* BORROWERS */}
              {/* <Route exact path='/borrowers' element={<Borrowers />}></Route> */}

              <Route
                exact
                path='/borrowers'
                element={
                  !isAuthenticated ? (
                    <Borrowers setAuth={setAuth} />
                  ) : (
                    <Navigate to='/borrower' />
                  )
                }
              ></Route>

              {/* BORROWER */}
              <Route path='/borrower'>
                <Route
                  exact
                  path='/borrower/:id'
                  element={<Borrower />}
                ></Route>
              </Route>

              {/* EDIT BORROWER */}
              <Route
                exact
                path='/editBorrower'
                element={
                  isAuthenticated ? (
                    <EditBorrower setAuth={setAuth} />
                  ) : (
                    <Navigate to='/borrower' />
                  )
                }
              ></Route>

              {/* ADD BORROWER */}
              <Route
                exact
                path='/addBorrower'
                element={
                  isAuthenticated ? (
                    <AddBorrower setAuth={setAuth} />
                  ) : (
                    <Navigate to='/borrowers' />
                  )
                }
              ></Route>

              {/* LOANS */}
              <Route exact path='/loans' element={<GetAllLoans />}></Route>

              {/* ADD LOANS */}
              <Route
                exact
                path='/addLoan'
                element={
                  isAuthenticated ? (
                    <AddLoan setAuth={setAuth} />
                  ) : (
                    <Navigate to='/loans' />
                  )
                }
              ></Route>
            </Routes>
          </Fragment>
        </div>
      </div>
    </Router>
  );
}

export default App;
